mapper.select:
select id, user_id, order_sn, order_status, consignee, mobile, address, message, goods_price, freight_price, coupon_price, integral_price, groupon_price, order_price, actual_price, pay_id, pay_time, ship_sn, ship_channel, ship_time, confirm_time, comments, end_time, add_time, update_time, deleted from cskaoyan_mall_order
and user_id = #{userId}
and order_sn = #{orderSn}
and order_status = #{status}
order by #{sort} ${order}
